mapper.select:
SELECT @rownum:= @rownum +1 rownum, fitting_id, outer_clothes_id, top_clothes_id, bottom_clothes_id, fitting_name, fitting_image, fitting_info, fitting_open_range, fitting_season, fitting_price FROM fitting WHERE
1=1
LIMIT #{startNum}, #{cri.amount}

mapper.update:
update fitting set outer_clothes_id =#{outer_clothes_id}, top_clothes_id =#{top_clothes_id}, bottom_clothes_id =#{bottom_clothes_id}, fitting_name =#{fitting_name}, fitting_info = #{fitting_info}, fitting_open_range =#{fitting_open_range},
fitting_image =#{fitting_image},
fitting_season =#{fitting_season}, fitting_price =#{fitting_price} where fitting_id = #{fitting_id}

mapper.delete:
delete from fitting
where fitting_id = #{fitting_id}
and member_id = #{member_id}

mapper.select:
SELECT @rownum:= @rownum +1 rownum, fitting_id, outer_clothes_id, top_clothes_id, bottom_clothes_id, fitting_name, fitting_image, fitting_info, fitting_open_range, fitting_season, fitting_price FROM fitting WHERE
1=1
AND fitting_open_range =#{openRange}
LIMIT #{startNum}, #{cri.amount}
