mapper.insert:
insert into member(
member_id, member_password,  member_name, member_sex, member_phone,
member_email, member_height, member_top_size, member_bottom_size, member_shoes_size, member_face,
member_grade, member_status
 )
values(
#{member_id}, #{member_password}, #{member_name}, #{member_sex}, #{member_phone},
#{member_email}, #{member_height}, #{member_top_size}, #{member_bottom_size}, #{member_shoes_size}, #{member_face}, 
#{member_grade}, #{member_status}
)

mapper.select:
select member_id, member_name, member_grade
from member
where member_id = #{member_id} and member_password= #{member_password}

select member_id, member_name, member_sex, member_phone, member_email, member_height, member_top_size, member_bottom_size,
	member_shoes_size, member_face, member_reg_date, member_grade
	from(
		select (@ROWNUM:=@ROWNUM+1) AS RNUM, member_id, member_name, member_sex, member_phone, member_email, member_height, member_top_size, member_bottom_size,
			member_shoes_size, member_face, member_reg_date, member_grade
		from(
			select member_id, member_name, member_sex, member_phone, member_email, member_height, member_top_size, member_bottom_size,
			member_shoes_size, member_face, member_reg_date, member_grade
			from member
			order by member_id asc
			)AS A, (SELECT @ROWNUM:=0) AS B
			)AS C
			where C.RNUM between #{startRow} and #{endRow}
