mapper.select:
select no, title, writer, writeDate, hit
from(
select rownum rnum, no, title, writer, writeDate, hit
from(
select no, title, writer, writeDate, hit
from board

order by no desc
)as a
)as b
where rnum between 0 and 7

mapper.update:
update board
set hit = hit +1
where no =#{no}

update board set title= #{title}, content
=#{content}, writer=#{writer}
where no = #{no}
