mapper.select:
		select member_id, member_name, member_birthday, member_phone, member_email, member_sex, gradeName, member_gradeNo, member_face
		from(	
			select (@ROWNUM:=@ROWNUM+1) AS RNUM, member_id, member_name, member_birthday, member_sex, member_phone, member_email, gradeName, member_gradeNo, member_face
			from(
				select member_id, member_password , member_name, member_birthday , member_sex , member_phone, member_email, 
				gradeName, member_gradeNo, member_face
				from member, grade
				where 1=1

				and member_gradeNo = gradeNo
				order by member_id asc
				) AS A, (SELECT @ROWNUM:=0) AS B

			) AS C
            where C.RNUM between #{startRow} and #{endRow}

mapper.update:
update member set title= #{title}, content
=#{content}, writer=#{writer}
where no = #{no}
